import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const Habits = () => {
  const [habits, setHabits] = useState([]);
  const [completedHabits, setCompletedHabits] = useState(new Set());
  const [newHabit, setNewHabit] = useState({ name: '', description: '', frequency: 'daily', target: '' });
  const [editingHabit, setEditingHabit] = useState(null);
  const [editForm, setEditForm] = useState({ name: '', description: '', frequency: 'daily', target: 1 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [completing, setCompleting] = useState(null);
  const [updating, setUpdating] = useState(false);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    if (user) {
      fetchHabits();
    } else {
      setLoading(false);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [user]);

  const getAuthConfig = () => {
    const token = localStorage.getItem('token');
    return {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };
  };

  const fetchHabits = async () => {
    try {
      setLoading(true);
      setError('');
      const config = getAuthConfig();
      const res = await axios.get('http://localhost:5000/api/habits', config);
      setHabits(res.data.data || []);
    } catch (err) {
      console.error('Error fetching habits:', err);
      if (err.response?.status === 401) {
        setError('Please login to view your habits');
      } else {
        setError('Failed to load habits');
      }
      setHabits([]);
    } finally {
      setLoading(false);
    }
  };

  const addHabit = async () => {
    if (!newHabit.name.trim()) {
      setError('Please enter a habit name');
      return;
    }

    try {
      setSubmitting(true);
      setError('');
      const config = getAuthConfig();
      await axios.post('http://localhost:5000/api/habits', newHabit, config);
      setNewHabit({ name: '', description: '', frequency: 'daily', target: '' });
      fetchHabits();
    } catch (err) {
      console.error('Error adding habit:', err);
      if (err.response?.status === 401) {
        setError('Please login to add habits');
      } else {
        setError('Failed to add habit');
      }
    } finally {
      setSubmitting(false);
    }
  };

  const updateHabit = async (habitId, updatedData) => {
    try {
      setUpdating(true);
      setError('');
      const config = getAuthConfig();
      const res = await axios.put(`http://localhost:5000/api/habits/${habitId}`, updatedData, config);
      if (res.data.success) {
        fetchHabits();
        setEditingHabit(null);
        setEditForm({ name: '', description: '', frequency: 'daily', target: '' });
      }
    } catch (err) {
      console.error('Error updating habit:', err);
      if (err.response?.status === 401) {
        setError('Please login to update habits');
      } else {
        setError('Failed to update habit');
      }
    } finally {
      setUpdating(false);
    }
  };

  const openEditModal = (habit) => {
    setEditingHabit(habit._id);
    setEditForm({
      name: habit.name,
      description: habit.description || '',
      frequency: habit.frequency,
      target: habit.target,
    });
  };

  const closeEditModal = () => {
    setEditingHabit(null);
    setEditForm({ name: '', description: '', frequency: 'daily', target: '' });
  };

  const saveEdit = () => {
    if (!editForm.name.trim()) {
      setError('Please enter a habit name');
      return;
    }
    updateHabit(editingHabit, editForm);
  };

  const deleteHabit = async (id) => {
    try {
      const config = getAuthConfig();
      await axios.delete(`http://localhost:5000/api/habits/${id}`, config);
      fetchHabits();
    } catch (err) {
      console.error('Error deleting habit:', err);
      setError('Failed to delete habit');
    }
  };

  const completeHabit = async (habitId) => {
    try {
      setCompleting(habitId);
      setError('');
      const config = getAuthConfig();
      const res = await axios.post(`http://localhost:5000/api/habits/${habitId}/complete`, {}, config);

      if (res.data.success) {
        // Add to completed habits set
        setCompletedHabits(prev => new Set([...prev, habitId]));
        // Refresh habits to get updated streak information
        fetchHabits();
        // Show success message with streak info
        const updatedHabit = res.data.data.habit;
        const streakMessage = updatedHabit.currentStreak > 1
          ? `ðŸ”¥ ${updatedHabit.currentStreak} day streak! Keep it up!`
          : 'Great start! Complete it again tomorrow to build your streak!';
        alert(`Habit completed successfully! ${streakMessage} ðŸŽ‰`);
      }
    } catch (err) {
      console.error('Error completing habit:', err);
      if (err.response?.status === 400) {
        setError('Habit already completed today');
      } else if (err.response?.status === 401) {
        setError('Please login to complete habits');
      } else {
        setError('Failed to complete habit');
      }
    } finally {
      setCompleting(null);
    }
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading your habits...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2>My Habits</h2>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Add New Habit</h5>
          <div className="row g-3">
            <div className="col-md-6">
              <input
                type="text"
                className="form-control"
                placeholder="Habit name (e.g., Exercise, Read, Meditate)"
                value={newHabit.name}
                onChange={(e) => setNewHabit({ ...newHabit, name: e.target.value })}
              />
            </div>
            <div className="col-md-6">
              <select
                className="form-select"
                value={newHabit.frequency}
                onChange={(e) => setNewHabit({ ...newHabit, frequency: e.target.value })}
              >
                <option value="daily">Daily</option>
                <option value="weekly">Weekly</option>
                <option value="monthly">Monthly</option>
              </select>
            </div>
            <div className="col-12">
              <textarea
                className="form-control"
                placeholder="Description (optional)"
                value={newHabit.description}
                onChange={(e) => setNewHabit({ ...newHabit, description: e.target.value })}
                rows="2"
              />
            </div>
            <div className="col-md-6">
              <input
                type="number"
                className="form-control"
                placeholder="Target (minutes per period)"
                value={newHabit.target}
                onChange={(e) => setNewHabit({ ...newHabit, target: e.target.value })}
                min="1"
              />
            </div>
            <div className="col-md-6">
              <button
                className="btn btn-primary w-100"
                onClick={addHabit}
                disabled={submitting}
              >
                {submitting ? 'Adding...' : 'Add Habit'}
              </button>
            </div>
          </div>
        </div>
      </div>

      <h3>Your Habits ({habits.length})</h3>
      {habits.length === 0 ? (
        <div className="alert alert-info">
          No habits yet. Create your first habit above!
        </div>
      ) : (
        <div className="row">
          {habits.map(habit => (
            <div key={habit._id} className="col-md-6 col-lg-4 mb-3">
              <div className="card h-100">
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">{habit.name}</h5>
                  {habit.description && (
                    <p className="card-text text-muted">{habit.description}</p>
                  )}
                  <div className="mt-auto">
                    <div className="d-flex justify-content-between align-items-center mb-2">
                      <span className="badge bg-primary">{habit.frequency}</span>
                      <span className="text-muted">Target: {habit.target} minutes</span>
                    </div>
                    <div className="d-flex justify-content-between align-items-center mb-2">
                      <span className="text-success fw-bold">
                        ðŸ”¥ {habit.currentStreak || 0} day streak
                      </span>
                      {habit.longestStreak > 0 && (
                        <span className="text-muted small">
                          Best: {habit.longestStreak} days
                        </span>
                      )}
                    </div>
                    <div className="d-flex gap-1">
                      <button
                        className={`btn btn-sm ${
                          completedHabits.has(habit._id)
                            ? 'btn-success'
                            : 'btn-outline-success'
                        }`}
                        onClick={() => completeHabit(habit._id)}
                        disabled={completing === habit._id || completedHabits.has(habit._id)}
                        style={{ flex: 2 }}
                      >
                        {completing === habit._id ? (
                          <span className="spinner-border spinner-border-sm" role="status"></span>
                        ) : completedHabits.has(habit._id) ? (
                          'âœ“ Completed'
                        ) : (
                          'Complete'
                        )}
                      </button>
                      <button
                        className="btn btn-outline-warning btn-sm"
                        onClick={() => openEditModal(habit)}
                        style={{ flex: 1 }}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-outline-danger btn-sm"
                        onClick={() => deleteHabit(habit._id)}
                        style={{ flex: 1 }}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Edit Habit Modal */}
      {editingHabit && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Habit</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={closeEditModal}
                ></button>
              </div>
              <div className="modal-body">
                <div className="row g-3">
                  <div className="col-md-6">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Habit name"
                      value={editForm.name}
                      onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                    />
                  </div>
                  <div className="col-md-6">
                    <select
                      className="form-select"
                      value={editForm.frequency}
                      onChange={(e) => setEditForm({ ...editForm, frequency: e.target.value })}
                    >
                      <option value="daily">Daily</option>
                      <option value="weekly">Weekly</option>
                      <option value="monthly">Monthly</option>
                    </select>
                  </div>
                  <div className="col-12">
                    <textarea
                      className="form-control"
                      placeholder="Description (optional)"
                      value={editForm.description}
                      onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
                      rows="2"
                    />
                  </div>
                  <div className="col-12">
                  <input
                    type="number"
                    className="form-control"
                    placeholder="Target (minutes per period)"
                    value={editForm.target}
                    onChange={(e) => setEditForm({ ...editForm, target: e.target.value })}
                    min="1"
                  />
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={closeEditModal}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={saveEdit}
                  disabled={updating}
                >
                  {updating ? 'Updating...' : 'Save Changes'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Habits;
