import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AIRecommendations = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecommendations = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('Please login to view AI recommendations');
          setLoading(false);
          return;
        }

        const res = await axios.get('http://localhost:5000/api/recommendations', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (res.data.success) {
          setRecommendations(res.data.data || []);
        } else {
          setError('Failed to load recommendations');
        }
      } catch (err) {
        console.error('Error fetching recommendations:', err);
        setError(err.response?.data?.error || 'Failed to load recommendations');
      } finally {
        setLoading(false);
      }
    };
    fetchRecommendations();
  }, []);

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading AI recommendations...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger">
          <h4>Error Loading Recommendations</h4>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2>AI Recommendations</h2>
      <p className="text-muted">Personalized suggestions to help you build better habits</p>

      {recommendations.length === 0 ? (
        <div className="alert alert-info">
          <h4>No Recommendations Yet</h4>
          <p>Create some habits and track your progress to get personalized AI recommendations!</p>
        </div>
      ) : (
        <div className="row">
          {recommendations.map((rec, index) => (
            <div key={index} className="col-md-6 mb-4">
              <div className={`card border-${rec.priority === 'high' ? 'danger' : rec.priority === 'medium' ? 'warning' : 'success'}`}>
                <div className="card-body">
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <h5 className="card-title">{rec.title}</h5>
                    <span className={`badge bg-${rec.priority === 'high' ? 'danger' : rec.priority === 'medium' ? 'warning' : 'success'}`}>
                      {rec.priority}
                    </span>
                  </div>
                  <p className="card-text">{rec.description}</p>
                  <div className="mt-3">
                    <small className="text-muted">
                      <i className="fas fa-lightbulb"></i> {rec.type.charAt(0).toUpperCase() + rec.type.slice(1)} Tip
                    </small>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AIRecommendations;
