import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [habits, setHabits] = useState([]);
  const [progress, setProgress] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const habitsRes = await axios.get('http://localhost:5000/api/habits');
      setHabits(habitsRes.data);
      const progressRes = await axios.get('http://localhost:5000/api/progress');
      setProgress(progressRes.data);
    };
    fetchData();
  }, []);

  return (
    <div>
      <h2>Dashboard</h2>
      <div className="row">
        <div className="col-md-6">
          <h3>Your Habits</h3>
          <ul className="list-group">
            {habits.map(habit => (
              <li key={habit._id} className="list-group-item">
                {habit.name} - {habit.frequency}
              </li>
            ))}
          </ul>
        </div>
        <div className="col-md-6">
          <h3>Progress Summary</h3>
          <p>Total Habits: {habits.length}</p>
          <p>Completed Today: {progress.completedToday || 0}</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
