import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Habits = () => {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState({ name: '', frequency: 'daily' });

  useEffect(() => {
    fetchHabits();
  }, []);

  const fetchHabits = async () => {
    const res = await axios.get('http://localhost:5000/api/habits');
    setHabits(res.data);
  };

  const addHabit = async () => {
    await axios.post('http://localhost:5000/api/habits', newHabit);
    setNewHabit({ name: '', frequency: 'daily' });
    fetchHabits();
  };

  const deleteHabit = async (id) => {
    await axios.delete(`http://localhost:5000/api/habits/${id}`);
    fetchHabits();
  };

  return (
    <div>
      <h2>Habits</h2>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Habit name"
          value={newHabit.name}
          onChange={(e) => setNewHabit({ ...newHabit, name: e.target.value })}
        />
        <select
          className="form-select mt-2"
          value={newHabit.frequency}
          onChange={(e) => setNewHabit({ ...newHabit, frequency: e.target.value })}
        >
          <option value="daily">Daily</option>
          <option value="weekly">Weekly</option>
        </select>
        <button className="btn btn-primary mt-2" onClick={addHabit}>Add Habit</button>
      </div>
      <ul className="list-group">
        {habits.map(habit => (
          <li key={habit._id} className="list-group-item d-flex justify-content-between">
            {habit.name} - {habit.frequency}
            <button className="btn btn-danger btn-sm" onClick={() => deleteHabit(habit._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Habits;
