import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const Dashboard = () => {
  const [habits, setHabits] = useState([]);
  const [progress, setProgress] = useState({});
  const [incompleteHabits, setIncompleteHabits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError('');

        const token = localStorage.getItem('token');
        if (!token) {
          setError('Please login to view your dashboard');
          setLoading(false);
          return;
        }

        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };

        const habitsRes = await axios.get('http://localhost:5000/api/habits', config);
        setHabits(habitsRes.data.data || []);

        const progressRes = await axios.get('http://localhost:5000/api/progress', config);
        setProgress(progressRes.data.data || {});

        // Fetch incomplete habits for today
        const incompleteRes = await axios.get('http://localhost:5000/api/progress/incomplete/today', config);
        setIncompleteHabits(incompleteRes.data.data || []);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        if (err.response?.status === 401) {
          setError('Please login to view your dashboard');
        } else if (err.response?.status === 404) {
          setError('Dashboard data not found. Please try again later.');
        } else {
          setError('Failed to load dashboard data');
        }
        setHabits([]);
        setProgress({});
        setIncompleteHabits([]);
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchData();
    } else {
      setLoading(false);
    }
  }, [user]);

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2>Dashboard</h2>

      {/* Incomplete Habits Notification */}
      {incompleteHabits.length > 0 && (
        <div className="alert alert-warning mb-4" role="alert">
          <h5 className="alert-heading">
            <i className="bi bi-exclamation-triangle-fill me-2"></i>
            Don't forget your habits today!
          </h5>
          <p className="mb-2">You have {incompleteHabits.length} habit{incompleteHabits.length > 1 ? 's' : ''} that haven't been completed yet:</p>
          <ul className="mb-0">
            {incompleteHabits.map(habit => (
              <li key={habit._id}>
                <strong>{habit.name}</strong>
                {habit.currentStreak > 0 && (
                  <span className="text-muted ms-2">
                    (Current streak: {habit.currentStreak} day{habit.currentStreak > 1 ? 's' : ''})
                  </span>
                )}
              </li>
            ))}
          </ul>
          <hr />
          <p className="mb-0">
            <a href="/habits" className="alert-link">Go to Habits page</a> to complete them and keep your streaks going!
          </p>
        </div>
      )}

      <div className="row">
        <div className="col-md-6">
          <h3>Your Habits</h3>
          {habits.length === 0 ? (
            <div className="alert alert-info">
              No habits found. <a href="/habits">Create your first habit</a>
            </div>
          ) : (
            <ul className="list-group">
              {habits.map(habit => (
                <li key={habit._id} className="list-group-item d-flex justify-content-between align-items-center">
                  <div>
                    <strong>{habit.name}</strong>
                    {habit.description && <p className="mb-0 text-muted small">{habit.description}</p>}
                  </div>
                  <span className="badge bg-primary rounded-pill">{habit.frequency}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
        <div className="col-md-6">
          <h3>Progress Summary</h3>
          <div className="card">
            <div className="card-body">
              <p className="card-text">
                <strong>Total Habits:</strong> {habits.length}
              </p>
              <p className="card-text">
                <strong>Completed Today:</strong> {habits.length - incompleteHabits.length}
              </p>
              <p className="card-text">
                <strong>Active Habits:</strong> {habits.filter(h => h.isActive !== false).length}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
